function onSubmit() {
	//check and ensure that permanent leave start date is a month after today at least
    var leaveType = g_form.getValue('leave_type');
    var startDateStr = g_form.getValue('start_date');
	var endDateStr = g_form.getValue('end_date');

    if (leaveType == 'pl' && startDateStr) {
		var today = new Date();
		var startDate = new Date(startDateStr);

		//add 30 days to today
		var minDate = new Date();
		minDate.setDate(today.getDate() + 30);

		if (startDate < minDate) 
		{
			alert('For Permanent Leave, start date must be at least 30 days from today.');
			//clear values
			g_form.setValue('start_date', '');
			g_form.setValue('end_date', '');
			return false; //prevent submission
		}
    }
	//check and ensure that the user has enough leave left to use 
	/*
    var start = new Date(startDateStr);
    var end = new Date(endDateStr);
    var diffTime = end - start; 
    var days = Math.floor(diffTime / (1000 * 60 * 60 * 24)) + 1; //1000 milliseconds, 60 seconds per minute, 60 min per hour, 24 hours a day + 1 to include start date

    var ga = new GlideAjax('CheckLeaveBalance'); //use the script include (server side)
    ga.addParam('sysparm_name', 'getBalance');
    ga.addParam('sysparm_leave_type', leaveType);
    ga.addParam('sysparm_days', days);

	/* request to execute the method and parameters associated with this GlideAjax object. The server processes the request asynchronously and returns the results via the function specified as the callback function. This example, which uses a callback function, is asynchronous meaning that the user's screen is not frozen while the script waits for a response to come back from the server. Asynchronous GlideAjax is recommended over synchronous.*/

	ga.getXMLAnswer(function(answer) 
	{
        var balance = parseInt(answer, 10);
        if (balance == -1) 
		{
            alert('Could not find your leave balance. Please contact admin.');
            return false;
        }
        if (balance < days) 
		{
            alert('Not enough ' + (leaveType == 'sl' ? 'sick' : 'planned') + ' leaves left. You have ' + balance + ', but requested ' + days + '.');
            return false;
        } 
		else 
		{
            // balance ok â†’ submit
            g_form.submit(); 
        }
    });


    return false; //wait for ajax script callback*/
	return true;
}
