sys_script.script:
(function executeRule(current, previous) 
{
	if (current.approval == 'approved' && previous.approval != 'approved') 
	{
		var days = gs.dateDiff(current.start_date, current.end_date, true) + 1;
        var leavesLeft = new GlideRecord('x_1804195_leave_ma_track_leaves');
        leavesLeft.addQuery('user', current.requested_by);
        leavesLeft.query();
        if (leavesLeft.next()) 
		{
            if (current.leave_type == 'sl') 
			{
                leavesLeft.sick_leaves -= days;
				gs.info("Subtracted " + days + ", The value of the sick remaining leaves is " + leavesLeft.sick_leaves); //logging
            } 
			else if (current.leave_type == 'pl') 
			{
                leavesLeft.planned_leaves -= days;
				gs.info("Subtracted " + days + ", The value of the remaining planned leaves is " + leavesLeft.sick_leaves); //logging 

            }
			//add user, sick left, planned left, and updated on
            leavesLeft.update();
        }
    }
})(current, previous);

(function executeRule(current, previous) 
{
    if (current.approval == 'approved' && previous.approval != 'approved') 
    {
        var days = gs.dateDiff(current.start_date, current.end_date, true) + 1;
        var leavesLeft = new GlideRecord('x_1804195_leave_ma_track_leaves');
        leavesLeft.addQuery('user', current.requested_by);
        leavesLeft.query();

        if (leavesLeft.next()) //record exists for this user so we can just deduct straight away
        {
            if (current.leave_type == 'sl') 
            {
                leavesLeft.sick_leaves -= days;
                gs.info("Subtracted " + days + ", remaining sick leaves: " + leavesLeft.sick_leaves);
            } 
            else if (current.leave_type == 'pl') 
            {
                leavesLeft.planned_leaves -= days;
                gs.info("Subtracted " + days + ", remaining planned leaves: " + leavesLeft.planned_leaves);
            }
            leavesLeft.updated_on = gs.nowDateTime(); // optional: track when updated
            leavesLeft.update();
        } 
        else 
        {
            // no record â†’ create new one
            leavesLeft.initialize();
            leavesLeft.user = current.requested_by;
            // give starting balances minus requested days
            if (current.leave_type == 'sl') 
            {
                leavesLeft.sick_leaves = 30 - days;
                leavesLeft.planned_leaves = 30;
            } 
            else if (current.leave_type == 'pl') 
            {
                leavesLeft.planned_leaves = 30 - days;
                leavesLeft.sick_leaves = 30;
            }
            leavesLeft.updated_on = gs.nowDateTime();
            leavesLeft.insert();
            gs.info("Created new leave balance record for user " + current.requested_by);
        }
    }
})(current, previous);
