sys_script_include.script:
var LeaveTrackerAjax = Class.create();
LeaveTrackerAjax.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    getRemainingLeaves: function() {
        var tracker = new GlideRecord('x_1804195_leave_ma_leave_tracker');
        tracker.addQuery('user', gs.getUserID());
        tracker.query();

        if (tracker.next()) {
            return 'Sick: ' + tracker.sick_leaves + ', Planned: ' + tracker.planned_leaves;
        } else {
            return 'Sick: 30, Planned: 60';
        }
    }
});
