sys_script_include.script:
//this is basically a getter
//to let me access the server side data i cant within a catalogue
//because these functions only run on the server, not the browser

/*The GlideAjax class is used by client-side scripts to send data to and receive data from the ServiceNow server. The client-side script passes parameters to the Script Include. The Script Include returns data as XML or a JSON object. The client-side script parses data from the response and can use the data in subsequent script logic.
*/

var CheckLeaveBalance = Class.create();
CheckLeaveBalance.prototype = Object.extendsObject(AbstractAjaxProcessor, {
	getBalance: function() 
	{
		//get needed data
        var userID = gs.getUserID();
        var leaveType = this.getParameter('sysparm_leave_type');
        var days = parseInt(this.getParameter('sysparm_days'), 10);

        var gr = new GlideRecord('x_1804195_leave_ma_track_leaves');
        gr.addQuery('user', userID);
        gr.query();
        if (gr.next()) 
		{
            if (leaveType == 'sl') 
			{
                return gr.sick_leaves;
            } 
			else if (leaveType == 'pl') 
			{
                return gr.planned_leaves;
            }
        }
        return -1; //means no record
    }
});
